import IngredientsList from "./IngredientsList";
import "./Main.css";
import { useState } from "react";
export default function Main() {
    const [ingredients, setIngredients] = useState(["all the main spices", "tomato paste", "pasta", "ground beef", ]);
    const [recipeShown, setRecipeShown] = useState(false);
    
    const ingredientsListItems = ingredients.map((ingredient, index) => (
        <li key={index}>{ingredient}</li>
    ))

    
    function addIngredient(formData){
        const newIngredient = formData.get("ingredient");
        setIngredients(prevIngredients => [...prevIngredients, newIngredient]);
    }

    return (
        <main>
            <form action ={addIngredient}className="add-ingredient-form">
                <input 
                    type="text"
                    placeholder="e.g. oregano"
                    aria-label="Add ingredient"
                    name = "ingredient"
                    autoFocus = {true}
                />
                <button>Add ingredient</button>
                </form>
                {ingredientsListItems.length > 0 &&
                <section>
                    <h2>Ingredients on hand:</h2>
                    <ul className="ingredients-list" aria-live="polite">{ingredientsListItems}</ul>
                    {ingredientsListItems.length > 3 &&
                    <div className="get-recipe-container">
                        <div>
                            <h3>Ready for a recipe?</h3>
                            <p>Generate a recipe from your list of ingredients.</p>
                        </div>
                        <button onClick={() => setRecipeShown(true)}>Get recipe</button>
                    </div>
                    }
                </section>
                }
                {recipeShown &&
                    <IngredientsList />
                }

            
        </main>
    );
}
